import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Button } from "@mui/material";
import "./Prescription.css";

export const Pres = () => {
  const data = useSelector((state) => state.Render.Render);
  const currentDate = new Date();
  const year = currentDate.getFullYear();
  const month = currentDate.getMonth() + 1;
  const day = currentDate.getDate();
  const formattedDate = `${year}-${month}-${day}`;

  const [showHomeRemedy, setShowHomeRemedy] = useState(false);

  const handleHomeRemedy = () => {
    setShowHomeRemedy(!showHomeRemedy);
  };

  return (
    <div className="prescription">
      <div className="top">
        <h2 className="text-center">Prescription</h2>
      </div>
      <div className="container mx-auto p-4">
        <div className="grid grid-cols-2 gap-4">
          <div>Name of the Patient:</div>
          <div>{data.Name}</div>
          <div>Date of Birth:</div>
          <div>{data.Dob}</div>
          <div>Age:</div>
          <div>{data.Age}</div>
          <div>Contact Number:</div>
          <div>{data.Mobile}</div>
          <div>Today's Date:</div>
          <div>{formattedDate}</div>
          <div>Diagnosed with:</div>
          <div>{data.Result.CommonlyUsedforDiseases}</div>
          <div className="col-span-2 flex justify-between items-center">
            <div>Ayurvedic Medicine</div>
            <div>Dosage (per day)</div>
            <div>
              {showHomeRemedy ? (
                <Button variant="contained" onClick={handleHomeRemedy}>
                  Hide Home Remedy
                </Button>
              ) : (
                <Button variant="contained" onClick={handleHomeRemedy}>
                  Show Home Remedy
                </Button>
              )}
            </div>
          </div>
          <div>{data.Result.MedicineName}</div>
          <div>{data.Result.Dosage}</div>
          <div className="col-span-3">
            {showHomeRemedy && <div>{data.Result.HomeRemedies}</div>}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Pres;
